import React, { Component } from 'react';
import { withStyles } from '@material-ui/core';
import { connect } from 'react-redux';
import { showEditForm } from './contactState';
import CloseIcon from '@material-ui/icons/Close';
import IconButton from '@material-ui/core/IconButton';

class EditContact extends Component {
    state = {  }
    handleCancle=()=>{
        this.props.showEditForm({id:"",YN:false})
    }
   handleOnchange=(e)=>{

   } 
    render() { 
        let classes=this.props.classes
        let details=this.props.store.editDetails
        return ( 
            <div className={classes.mainContainer}>
                <div className={classes.cancleButtonContainer}>
                    <IconButton onClick={this.handleCancle}><CloseIcon /></IconButton>
                </div>
            <div className={classes.formContainer}>
                <div>
                    <div>
                        <label>Name</label>
                        <input type="text" 
                      className={`form-control`} 
                      name="firstName"
                      value={details.name || details.name}
                      onChange={this.handleOnchange}/>
                    </div>
                    <div>
                        <label>Last Name</label>
                        <input type="text" 
                      className={`form-control`} 
                      name="lastName"

                      onChange={this.handleOnchange}/>
                    </div>
                    <div>
                        <label>Email</label>
                        <input type="text" 
                      className={`form-control`} 
                      name="email"
                      onChange={this.handleOnchange}/>
                    </div>
                    <div>
                        <label>Phone</label>
                        <input type="text" 
                      className={`form-control`} 
                      name="phone"
                      onChange={this.handleOnchange}/>
                    </div>
                </div>
                <div className={classes.saveButtonContainer}>
                <button type="button" className="btn btn-primary" onClick={this.handleSave}>Save</button>
                </div>
            </div>
            </div>
         );
    }
}
const styles=theme=>({
mainContainer:{
    position:"absolute",
    right:"110px",
    bottom:"-46px"
},
formContainer:{
    width:"300px",
    height:"360px",
    background:"#39ac73",
    padding:"20px",
    borderRadius:"15px"
},
cancleButtonContainer:{
    float:"right"
},
saveButtonContainer:{
    textAlign: "center",
    margin: "12px"
},
})

const mapStateToProps=(state)=>{
    return{
        store:state.contacts
    }
}
const mapDispatchToProps={
    showEditForm

} 

export default withStyles(styles)(connect(mapStateToProps,mapDispatchToProps)(EditContact));
