import React, { Component } from 'react';
import { withStyles } from '@material-ui/core';
import { connect } from 'react-redux';
import { showEditForm } from './contactState';
import CloseIcon from '@material-ui/icons/Close';
import IconButton from '@material-ui/core/IconButton';

class EditContact extends Component {
    state = {  }
    handleCancle=()=>{
        this.props.showEditForm({id:"",YN:false})
    }
    render() { 
        let classes=this.props.classes
        return ( 
            <div className={classes.mainContainer}>
            <div className={classes.formContainer}>
                <div className={classes.cancleButtonContainer}>
                    <IconButton onClick={this.handleCancle}><CloseIcon /></IconButton>
                </div>
                <div>
                    <div>
                        <label>Name</label>
                        <input type="text" 
                      className={`form-control`} 
                      name="firstName"
                      onChange={this.handleOnchange}/>
                    </div>
                    <div>
                        <label>Last Name</label>
                        <input type="text" 
                      className={`form-control`} 
                      name="lastName"

                      onChange={this.handleOnchange}/>
                    </div>
                    <div>
                        <label>Email</label>
                        <input type="text" 
                      className={`form-control`} 
                      name="email"
                      onChange={this.handleOnchange}/>
                    </div>
                    <div>
                        <label>Phone</label>
                        <input type="text" 
                      className={`form-control`} 
                      name="phone"
                      onChange={this.handleOnchange}/>
                    </div>
                </div>
            </div>    
            </div>
         );
    }
}
const styles=theme=>({
mainContainer:{
    position:"absolute",
    right:"110px",
    bottom:"-46px"
},
formContainer:{
    width:"300px",
    height:"200px",
    background:"red",

},
cancleButtonContainer:{
    float:"right"
},
})

const mapStateToProps=(state)=>{
    return{
        store:state.contacts
    }
}
const mapDispatchToProps={
    showEditForm

} 

export default withStyles(styles)(connect(mapStateToProps,mapDispatchToProps)(EditContact));
